import React, { useEffect } from 'react'

const SerachResult = ({searchInput}) => {

    let RecData = [];
    return (

    useEffect(() => {

        console.log("What is search :: ", searchInput);

        let data = [];

        const API = `https://forkify-api.herokuapp.com/api/v2/recipes?search=${searchInput}&key=6f023f1f-818d-40d7-a0a0-f095148f22ae`;

        async function GetData(e) {
            try{
            console.log("Getting Data")
            const res = await fetch(API);
            data = await res.json();
            RecData = await data.data.recipes;
            console.log("The Data::",RecData[2]["title"]);
            //   console.log("The Data::",data.data.rercipes[0].publisher);

            console.log("Accessible or not?",RecData)
            // RecData.map((index, recipes) => {
                return(
                <ul className="results">
                    <li className="preview">
                    <a className="preview__link preview__link--active" href="#23456">
                        <figure className="preview__fig">
                        <img src={RecData[1].image_url} alt="Test" />
                        </figure>
                        <div className="preview__data">
                        <h4 className="preview__title">{RecData[1].title}</h4>
                        <p className="preview__publisher">{RecData[1].publisher}</p>
                        <div className="preview__user-generated">
                            <svg>
                            {/* <use href="src/img/icons.svg#icon-user" /> */}
                            </svg>
                        </div>
                        </div>
                    </a>
                    </li>
                </ul>
            // )}
            )

            }
            catch(e){
                console.log("Error Occured : ",e);
            }
        }

        GetData();
    }, [searchInput])
  )
}

export default SerachResult
