const SerachResult = ({searchInput}) => {


    useEffect(() => {
        console.log("Mounted")
        GetData()
    }, [searchInput])


    console.log("data", searchInput);

    const data = [];

    const API = `https://forkify-api.herokuapp.com/api/v2/recipes?search=${searchInput}&key=6f023f1f-818d-40d7-a0a0-f095148f22ae`;

    async function GetData(e) {
      const res = await fetch(API);
      data = await res.json();
      console.log(data);
    }  

  return (
    <>
    <div className="search-results">
    {data.map((index, recipes) => {
        return(
        <ul className="results">
            <li className="preview">
            <a className="preview__link preview__link--active" href="#23456">
                <figure className="preview__fig">
                <img src={recipes.image_url[index]} alt="Test" />
                </figure>
                <div className="preview__data">
                <h4 className="preview__title">{recipes.title[index]}</h4>
                <p className="preview__publisher">{recipes.publisher[index]}</p>
                <div className="preview__user-generated">
                    <svg>
                    {/* <use href="src/img/icons.svg#icon-user" /> */}
                    </svg>
                </div>
                </div>
            </a>
            </li>
        </ul>
    )})}
        </div>
            <div className="pagination">
            <button className="btn--inline pagination__btn--prev">
            <svg className="search__icon">
                {/* <use href="src/img/icons.svg#icon-arrow-left" /> */}
            </svg>
            <span>Page 1</span>
            </button>
            <button className="btn--inline pagination__btn--next">
            <span>Page 3</span>
            <svg className="search__icon">
                {/* <use href="src/img/icons.svg#icon-arrow-right" /> */}
            </svg>
            </button> 
        </div>
